import React from 'react';
import { createDocument } from './mong';

function Contact() {

  const buttonStyle = { 
    margin: "8px 0", 
    borderRadius: "5px", 
    backgroundColor: "#27ae60", 
    color: "white", 
    border: "none", 
    width: '100px',
    padding: '10px 20px',
    cursor: 'pointer',
    transition: 'background-color 0.3s ease',
    ":hover": {
      backgroundColor: '#0062cc'
    }
  };

  return (
    <section id="contact" >
      <h2 style={{ display: "flex", alignItems: "center", flexDirection: "column", color: '#000000' }}>Contact Me</h2>
      <form>
      <div style={{ display: "flex", alignItems: "center", flexDirection: "column", color: '#000000' }}>
        <label htmlFor="name">Name</label>
        <input name='name' type="text" id="name" style={{ padding: "8px", margin: "8px 0", borderRadius: "5px", border: "1px solid #ccc", width: '350px' }} />
        <label htmlFor="email">Email</label>
        <input name='email' type="email" id="email" style={{ padding: "8px", margin: "8px 0", borderRadius: "5px", border: "1px solid #ccc", width: '350px' }} />
        <label htmlFor="message">Message</label>
        <textarea name='message' id="message" style={{ padding: "8px", margin: "8px 0", borderRadius: "5px", border: "1px solid #ccc", width: '350px' }}></textarea>
        <button onClick={createDocument()} type="submit" style={buttonStyle}>Submit</button>
      </div>
    </form>
    </section>
  );
}

export default Contact;
