import React from 'react';
import aboutBG from './about_background.jpg';

function About() {
  return (
    <section id="about" style={{ 
      backgroundImage: 'https://img.freepik.com/free-vector/desktop-smartphone-app-development_23-2148683810.jpg?w=740&t=st=1680694048~exp=1680694648~hmac=b932557dd245c23cd24e12586e93df8051df6895c01c60238f009702ae5932b7',
      backgroundSize: 'cover',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'flex-end',
      flexDirection: 'column',
      padding: '0 20%',
      color: 'Black'
    }}>
      <h2 style={{ textAlign: 'left', color: '#000000' }}>Badal Chauhan</h2>
      <p style={{ textAlign: 'left' }}>I am a Full Stack Web Developer and an UI/UX Designer. I am Passionate, resilient, and quick learner. Able to effectively self-manage during independent projects, as well as collaborate in a team setting. Equipped with a diverse and promising skill-set.</p>
      <button style={{ alignSelf: 'flex-start', marginTop: '15px', padding: "8px", borderRadius: "5px", backgroundColor: "#27ae60", color: "white", border: "none" }}>Download Resume</button>
    </section>
  );
}

export default About;
