import React from 'react';
import styled from 'styled-components';

import ProgressBar from './ProgressBar';
import ProjectCarousel from './ProjectCarousel';

const HomeWrapper = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
`;

const SectionWrapper = styled.div`
  width: 80%;
  margin-bottom: 40px;

  &:last-child {
    margin-bottom: 0;
  }
`;

const SectionTitle = styled.h2`
  font-size: 28px;
  margin-top: 0;
  margin-bottom: 20px;
`;

const AboutMeWrapper = styled.div`
  display: flex;
  flex-wrap: wrap;
  justify-content: space-between;
`;

const AboutMeTextWrapper = styled.div`
  width: calc(50% - 20px);
`;

const AboutMeTitle = styled.h3`
  font-size: 20px;
  margin-top: 0;
  margin-bottom: 10px;
`;

const AboutMeDescription = styled.p`
  font-size: 14px;
  margin-top: 0;
  margin-bottom: 10px;
`;

const AboutMeSkillsWrapper = styled.div`
  width: calc(50% - 20px);
`;

const SkillTitle = styled.h4`
  font-size: 16px;
  margin-top: 0;
  margin-bottom: 10px;
`;

const FooterWrapper = styled.div`
  background-color: #212121;
  color: #fff;
  display: flex;
  justify-content: center;
  align-items: center;
  height: 60px;
  width: 100%;
  position: absolute;
  bottom: 0;
  left: 0;
`;

const FooterText = styled.p`
  font-size: 14px;
  margin: 0;
`;

function Home() {
  const skills = [    { name: 'HTML', progress: 90 },    { name: 'CSS', progress: 80 },    { name: 'JavaScript', progress: 70 },    { name: 'React', progress: 60 },    { name: 'Node.js', progress: 50 },    { name: 'MongoDB', progress: 40 },  ];

  const projects = [    {      title: 'Project 1',      description: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit. Sed sit amet massa vel ex molestie sagittis. Donec mollis fringilla orci, eu volutpat elit. Fusce euismod neque a odio pretium, in varius quam sagittis.',      image: 'https://picsum.photos/500/300',      link: '#',    },    {      title: 'Project 2',      description: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit. Sed sit amet massa vel ex molestie sagittis. Donec mollis fringilla orci, eu volutpat elit. Fusce euismod neque a odio pretium, in varius quam sagittis.',      image: 'https://picsum.photos/500/300',      link: '#',    },    {      title: 'Project 3',      description: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit. Sed sit amet massa vel ex molestie sagittis. Donec mollis fringilla orci, eu volutpat elit. Fusce euismod neque a odio pretium, in varius quam sagittis.',      image: 'https://picsum.photos/500/300',      link: '#',    },    {      title: 'Project 4',      description: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit. Sed sit amet massa vel ex molestie sagittis. Donec mollis fringilla orci, eu volutpat elit. Fusce euismod neque a odio pretium, in varius quam sagittis.',
  image: 'https://picsum.photos/500/300',
  link: '#',
  },
  ];
  
return (
  <HomeWrapper>
    <SectionWrapper>
        <SectionTitle>About Me</SectionTitle>
            <AboutMeWrapper>
                <AboutMeTextWrapper>
                    <AboutMeTitle>Hi, I'm John</AboutMeTitle>
                    <AboutMeDescription>
                        I'm a web developer with 3 years of experience in developing websites and web applications. My main focus is on front-end development using React and other modern web technologies.
                    </AboutMeDescription>
                </AboutMeTextWrapper>
                <AboutMeSkillsWrapper>
                    <SkillTitle>Skills</SkillTitle>
                        {skills.map((skill) => (
                            <ProgressBar key={skill.name} name={skill.name} progress={skill.progress} />
                        ))}
                </AboutMeSkillsWrapper>
            </AboutMeWrapper>
    </SectionWrapper>
    <SectionWrapper>
        <SectionTitle>Projects</SectionTitle>
        <ProjectCarousel projects={projects} />
    </SectionWrapper>

    <FooterWrapper>
        <FooterText>&copy; 2023 John Doe. All rights reserved.</FooterText>
    </FooterWrapper>
</HomeWrapper>
);
}

export default Home;
