import React, { useState } from 'react';
import axios from 'axios';

function Contact() {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [message, setMessage] = useState('');

  const buttonStyle = {
    margin: '8px 0',
    borderRadius: '5px',
    backgroundColor: '#27ae60',
    color: 'white',
    border: 'none',
    width: '100px',
    padding: '10px 20px',
    cursor: 'pointer',
    transition: 'background-color 0.3s ease',
    ':hover': {
      backgroundColor: '#0062cc'
    }
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      const response = await axios.post('http://localhost:3000/api/contact', {
        name,
        email,
        message
      });
      console.log(response.data.message);
      // Clear form inputs on successful submission
      setName('');
      setEmail('');
      setMessage('');
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <section id="contact">
      <h2 style={{ display: 'flex', alignItems: 'center', flexDirection: 'column', color: '#000000' }}>Contact Me</h2>
      <form onSubmit={handleSubmit}>
        <div style={{ display: 'flex', alignItems: 'center', flexDirection: 'column', color: '#000000' }}>
          <label htmlFor="name">Name</label>
          <input type="text" id="name" value={name} onChange={(event) => setName(event.target.value)} style={{ padding: "8px", margin: "8px 0", borderRadius: "5px", border: "1px solid #ccc", width: '350px' }} />
          <label htmlFor="email">Email</label>
         <input type="email" id="email" value={email} onChange={(event) => setEmail(event.target.value)} style={{ padding: '8px', margin: '8px 0', borderRadius: '5px', border: '1px solid #ccc', width: '350px' }} />
         <label htmlFor="message">Message</label>
         <textarea id="message" value={message} onChange={(event) => setMessage(event.target.value)} style={{ padding: '8px', margin: '8px 0', borderRadius: '5px', border: '1px solid #ccc', width: '350px' }}></textarea>
         <button type="submit" style={buttonStyle}>
           Submit
         </button>
       </div>
     </form>
   </section>
 );
}

export default Contact;
