import React, { useState } from "react";
import axios from "axios";

function Contact() {
  const buttonStyle = {
    margin: "8px 0",
    borderRadius: "5px",
    backgroundColor: "#27ae60",
    color: "white",
    border: "none",
    width: "100px",
    padding: "10px 20px",
    cursor: "pointer",
    transition: "background-color 0.3s ease",
  };

  const [user, setUser] = useState({
    name: "",
    email: "",
    message: "",
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setUser({
      ...user,
      [name]: value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const { name, email, message } = user;
    if (name && email && message) {
      axios
        .post("http://localhost:3001/contact", user)
        .then((res) => {
          console.log(res);
          alert("Successfully Submitted");
        })
        .catch((error) => {
          console.log(error);
          alert("Error submitting the form");
        });
    } else {
      alert("Please fill in all fields");
    }
  };

  return (
    <section id="contact">
      <h2
        style={{
          display: "flex",
          alignItems: "center",
          flexDirection: "column",
          color: "#000000",
        }}
      >
        Contact Me
      </h2>
      <form onSubmit={handleSubmit}>
        <div
          style={{
            display: "flex",
            alignItems: "center",
            flexDirection: "column",
            color: "#000000",
          }}
        >
          <label htmlFor="name">Name</label>
          <input
            type="text"
            id="name"
            style={{
              padding: "8px",
              margin: "8px 0",
              borderRadius: "5px",
              border: "1px solid #ccc",
              width: "350px",
            }}
            name="name"
            value={user.name}
            onChange={handleChange}
          />
          <label htmlFor="email">Email</label>
          <input
            type="email"
            id="email"
            style={{
              padding: "8px",
              margin: "8px 0",
              borderRadius: "5px",
              border: "1px solid #ccc",
              width: "350px",
            }}
            name="email"
            value={user.email}
            onChange={handleChange}
          />
          <label htmlFor="message">Message</label>
          <textarea
            id="message"
            style={{
              padding: "8px",
              margin: "8px 0",
              borderRadius: "5px",
              border: "1px solid #ccc",
              width: "350px",
            }}
            name="message"
            value={user.message}
            onChange={handleChange}
          ></textarea>
          <button type="submit" style={buttonStyle}>
            Submit
          </button>
        </div>
      </form>
    </section>
  );
}

export default Contact;
