import React, { useState } from 'react';

const projectData = [
  {
    id: 1,
    title: 'Project 1',
    description: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit. Vivamus euismod mollis risus a lobortis.',
    imageUrl: './images/project1.jpg',
    projectUrl: 'https://www.project1.com',
  },
  {
    id: 2,
    title: 'Project 2',
    description: 'Nullam eget mi elementum, bibendum ex quis, blandit nulla. Quisque eu libero varius, gravida orci non, ultricies ipsum.',
    imageUrl: '/images/project2.jpg',
    projectUrl: 'https://www.project2.com',
  },
  {
    id: 3,
    title: 'Project 3',
    description: 'Curabitur a magna vitae diam rutrum suscipit sed nec leo. Sed eget eros id mauris luctus pharetra.',
    imageUrl: '/images/project3.jpg',
    projectUrl: 'https://www.project3.com',
  },
];

function Carousel() {
  const [activeIndex, setActiveIndex] = useState(0);

  const handlePrev = () => {
    setActiveIndex((prevIndex) => (prevIndex === 0 ? projectData.length - 1 : prevIndex - 1));
  };

  const handleNext = () => {
    setActiveIndex((prevIndex) => (prevIndex === projectData.length - 1 ? 0 : prevIndex + 1));
  };

  return (
    <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', flexDirection: 'column' }}>
      <h2 style={{ marginBottom: '2rem' }}>Projects</h2>
      <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', marginBottom: '2rem' }}>
        <button style={{ padding: '1rem', marginRight: '1rem' }} onClick={handlePrev}>
          Prev
        </button>
        <div style={{ width: '50%', margin: '0 1rem' }}>
          <img src={projectData[activeIndex].imageUrl} alt={`Project ${activeIndex + 1}`} style={{ width: '100%' }} />
        </div>
        <button style={{ padding: '1rem', marginLeft: '1rem' }} onClick={handleNext}>
          Next
        </button>
      </div>
      <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center' }}>
        {projectData.map((project, index) => (
          <div
            key={project.id}
            onClick={() => setActiveIndex(index)}
            style={{
              backgroundColor: index === activeIndex ? '#0074d9' : '#e4e4e4',
              padding: '0.5rem',
              marginRight: '1rem',
              cursor: 'pointer',
              borderRadius: '0.5rem',
            }}
          >
            {project.title}
          </div>
        ))}
      </div>
      <a href={projectData[activeIndex].projectUrl} target="_blank" style={{ marginTop: '2rem', color: '#0074d9' }}>
        View Project
      </a>
    </div>
  );
}

export default Carousel;
