import React, { useState } from 'react';
import axios from 'axios';

function Contact() {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [message, setMessage] = useState('');

  const buttonStyle = { 
    margin: "8px 0", 
    borderRadius: "5px", 
    backgroundColor: "#27ae60", 
    color: "white", 
    border: "none", 
    width: '100px',
    padding: '10px 20px',
    cursor: 'pointer',
    transition: 'background-color 0.3s ease',
    ":hover": {
      backgroundColor: '#0062cc'
    }
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    axios.post('/submit', { name, email, message })
      .then(response => {
        console.log(response.data);
      })
      .catch(error => {
        console.log(error);
      });
  };

  return (
    <section id="contact" >
      <h2 style={{ display: "flex", alignItems: "center", flexDirection: "column", color: '#000000' }}>Contact Me</h2>
      <form onSubmit={handleSubmit}>
        <div style={{ display: "flex", alignItems: "center", flexDirection: "column", color: '#000000' }}>
          <label htmlFor="name">Name</label>
          <input type="text" id="name" style={{ padding: "8px", margin: "8px 0", borderRadius: "5px", border: "1px solid #ccc", width: '350px' }} value={name}          onChange={(event) => setName(event.target.value)} />
         <label htmlFor="email">Email</label>
         <input type="email" id="email" style={{ padding: "8px", margin: "8px 0", borderRadius: "5px", border: "1px solid #ccc", width: '350px' }} value={email} onChange={(event) => setEmail(event.target.value)} />
         <label htmlFor="message">Message</label>
         <textarea id="message" style={{ padding: "8px", margin: "8px 0", borderRadius: "5px", border: "1px solid #ccc", width: '350px' }} value={message} onChange={(event) => setMessage(event.target.value)}></textarea>
         <button type="submit" style={buttonStyle}>Submit</button>
       </div>
     </form>
   </section>
 );
}

export default Contact;
